import io.restassured.http.ContentType;
import org.json.JSONObject;
import org.junit.Test;

import static io.restassured.RestAssured.given;

public class TestAula_18 {

    @Test
    public void givenThatIPerformAPostRequest_whenIGetTheResponseCode_thenTheResponseStatusCodeIs200() {
        JSONObject jsonObject = new JSONObject();
        jsonObject.put("thumbnail", "data:image/jpeg;base64,");
        jsonObject.put("name", "Whatever");
        jsonObject.put("realName", "Spock");
        jsonObject.put("location", "Turma 1");
        jsonObject.put("isAlive", true);

        given()
                .header("Content-Type", "application/json")
                .contentType(ContentType.JSON)
                .body(jsonObject.toString())
                .when()
                .post("https://restool-sample-app.herokuapp.com/api/character")
                .then()
                .statusCode(200);
    }
}
